function Serpent()
        svo.tree.serpentdangerousaffs = {
                desc = "Fighting a serpent and have hypo,darkshade,impatience",

                function()
                        if svo.enabledclasses.serpent and svo.affl.darkshade and svo.affl.hypochondria and svo.affl.impatience then
                                return true
                        end
                end
        }

		svo.tree.impdarknoherb = {
                desc = "Fighting a serpent and have darkshade,impatience and off herb balance",

                function()
                        if svo.enabledclasses.serpent and svo.affl.darkshade and svo.affl.impatience and not svo.bals.herb then
                                return true
                        end
                end
        }
end

