if checkCuredAfflictions({"aeon"},possibleAffs) then removeEnemyAff("aeon") changeBombTimer("8") elseif checkCuredAfflictions({"slickness"},possibleAffs) then removeEnemyAff("slickness") elseif checkCuredAfflictions({"manaleech"},possibleAffs) then removeEnemyAff("manaleech") elseif checkCuredAfflictions({"slickness","aeon"},possibleAffs) then removeEnemyAff("aeon") changeBombTimer("8") end end

