function ndb.isvalidcity(which) which = which:title() return table.contains(ndb.valid.cities, which) and true or false end
function ndb.isvalidclass(which) which = which:lower() return table.contains(ndb.valid.classes, which) and true or false end
function ndb.isvalidrace(which) which = which:lower() return table.contains(ndb.valid.races, which) and true or false end

ndb.classhits = ndb.classhits or {}
function ndb.setfromclass(_, class, name) -- a name isn't always given if it is not available if not name then return end
class = class:lower()
ndb.classhits[name] = ndb.classhits[name] or {hits = 0, class = class}
if ndb.classhits[name].class ~= class then ndb.classhits[name].hits = 0 -- reset on a hit from the same person on another class else ndb.classhits[name].hits = ndb.classhits[name].hits + 1
if ndb.classhits[name].hits >= svo.conf.autoclassset then if class == "dragon" then if not ndb.isdragon(name) then ndb.setdragon(name, true) svo.echof("Auto-set %s as a Dragon.", name) end else local oldclass = ndb.getclass(name) if not oldclass then return end -- if the name isn't known, don't add it - could be fake names from illusions
if class ~= oldclass then ndb.setclass(name, class) svo.echof("Auto-set %s's class to %s.", name, class:title()) end end
ndb.classhits[name].hits = 0 -- reset, so we aren't querying the db on every hit end end end

function ndb.showhonorswindow(text)
  ndb.autohonorslabel = ndb.autohonorslabel or Geyser.Label:new({
    name = "ndb.autohonorslabel",
    x = "-340px", y = "-35px",
    width = "320px", height = "25px",
  })

  ndb.autohonorslabel:setStyleSheet([[
    margin: 0px;
    padding: 2px;

    background: rgba(0, 0, 51, 75%);
    border: none;
    border-radius: 4px;

    qproperty-alignment: 'AlignLeft | AlignVCenter';
    qproperty-wordWrap: true;
    font-family: 'Ubuntu','Calibri',serif;
  ]])

  ndb.autohonorslabel:show()
  ndb.autohonorslabel:echo([[<p style="color: grey; font-size: 15px;">]]..text..[[</p>]])
end

function ndb.hidehonorswindow()
  if ndb.autohonorslabel then ndb.autohonorslabel:hide() end
end

function ndb.onshow() local c = table.size(svo.me.highlightignore)
svo.echofn("# of people on the highlightignore: %d ", c)
setFgColor(unpack(svo.getDefaultColorNums)) setUnderline(true) echoLink("(view)", 'echo"\\n" expandAlias"vshow highlightignore"', 'Click here open the highlightignore list menu', true) echo"\n" end

function ndb.loadsettings()
  ndb.conf = ndb.conf or {}
  ndb.conf.citypolitics = ndb.conf.citypolitics or {}

  local conf_path = getMudletHomeDir() .. "/svo/namedb/citypolitics"

  if lfs.attributes(conf_path) then
    table.load(conf_path, ndb.conf.citypolitics)
  end

  -- setup defaults
  if next(ndb.conf.citypolitics) then return end

  -- include own city in table in case of org switch
  if svo.conf.org == "Shallam" then svo.config.set("org", "Targossas") end

  if svo.conf.org == "Targossas" then
    ndb.conf.citypolitics = {
      Ashtan = "enemy", Hashan = "enemy", Eleusis = "neutral", Cyrene = "ally", Mhaldor = "enemy", Targossas = "ally"
    }
  elseif svo.conf.org == "Mhaldor"  then
    ndb.conf.citypolitics = {
      Ashtan = "enemy", Hashan = "enemy", Eleusis = "enemy", Cyrene = "enemy", Targossas = "enemy", Mhaldor = "ally"
    }
  elseif svo.conf.org == "Ashtan"  then
    ndb.conf.citypolitics = {
      Mhaldor = "enemy", Hashan = "neutral", Eleusis = "enemy", Cyrene = "enemy", Targossas = "enemy", Ashtan = "ally"
    }
  elseif svo.conf.org == "Hashan"  then
    ndb.conf.citypolitics = {
      Mhaldor = "enemy", Ashtan = "neutral", Eleusis = "neutral", Cyrene = "neutral", Targossas = "enemy", Hashan = "ally"
    }
  elseif svo.conf.org == "Cyrene"  then
    ndb.conf.citypolitics = {
      Mhaldor = "neutral", Ashtan = "neutral", Eleusis = "neutral", Hashan = "neutral", Targossas = "ally", Cyrene = "ally"
    }
  elseif svo.conf.org == "Eleusis"  then
    ndb.conf.citypolitics = {
      Mhaldor = "enemy", Ashtan = "enemy", Cyrene = "neutral", Hashan = "neutral", Targossas = "neutral", Eleusis = "ally"
    }
  else
    ndb.conf.citypolitics = {
      Mhaldor = "neutral", Ashtan = "neutral", Cyrene = "neutral", Hashan = "neutral", Targossas = "neutral", Eleusis = "neutral"
    }
  end
end

function svo.add_peopletracker_config() svo.me.watchfor = svo.me.watchfor or {}
svo.config.setoption("watchfor", { type = "string", check = function(what) if what:find("^%w+$") then return true end end, onset = function () local name = string.title(svo.conf.watchfor) -- we want nil, not false so 'or' doesn't help if svo.me.watchfor[name] then svo.me.watchfor[name] = nil else svo.me.watchfor[name] = true end
if svo.me.watchfor[name] then svo.echof("Added <0,255,0>%s%s to the watchfor list - use qwhow to see them.", name, svo.getDefaultColor()) else svo.echof("Removed %s from the watchfor list.", name) end raiseEvent("svo watchfor name changed", name) end }) end
