if match then local title = line:gsub(" %(off channel%)", "")
temp_name_list[#temp_name_list + 1] = { name = match[1], title = title }
-- if we know a class, add it in local class = ndb.getclass(match[1]) local dragon if ndb.isdragon then dragon = ndb.isdragon(ndb.findname(line)) end
if class and class ~= "" then class = class:title() echo((" "):rep(70-#line)) cecho("<a_darkwhite>("..class..")") ndb.temp_classes_list[class] = (ndb.temp_classes_list[class] or 0) + 1 if dragon then cecho(" <a_darkwhite>(Dragon)") temp_dragon = temp_dragon +1 end elseif dragon then echo((" "):rep(70-#line)) cecho("<a_darkwhite>(Dragon)") temp_dragon = temp_dragon +1 end end

temp_name_list = {}
temp_dragon = 0
ndb.temp_classes_list = {}

if matches[2] == "Total" then return end

local class = line:sub(47):match("^(%w+)")
if class and class:lower() == "dragon" then class = nil end

temp_name_list[#temp_name_list + 1] = {
  name = matches[2],
  class = class,
  order = gmcp.Char.Status.order:match("(%w+)")
}

bdayTable = bdayTable or {}
bdayTable[#bdayTable+1] = matches[2]

svo.prompttrigger("namedb capture birtdhdays", function()
	local temp_name_list = {}

	for i,j in ipairs(bdayTable) do
		temp_name_list[#temp_name_list + 1] = {
			name = j
		}
	end

	db:merge_unique(ndb.db.people, temp_name_list)

	raiseEvent("NameDB got new data")
	bdayTable = nil
end)

temp_name_list = {}
temp_dragon = 0
ndb.temp_classes_list = {}

local match = getColorWildcard(14) line = line:trim() if match then temp_name_list[#temp_name_list + 1] = { name = match[1], title = line }
-- if we know a class, add it in local class = ndb.getclass(match[1]) local dragon if ndb.isdragon then dragon = ndb.isdragon(ndb.findname(line)) end
if class and class ~= "" then class = class:title() echo((" "):rep(70-#line)) cecho("<a_darkwhite>("..class..")") ndb.temp_classes_list[class] = (ndb.temp_classes_list[class] or 0) + 1 if dragon then cecho(" <a_darkwhite>(Dragon)") temp_dragon = temp_dragon +1 end elseif dragon then echo((" "):rep(60-#line)) cecho("<a_darkwhite>(Dragon)") temp_dragon = temp_dragon +1 end end

ndb.temp_name_list[#ndb.temp_name_list + 1] = {
  name = matches[2]
}

